package floats

import (
    "math"
    "testing"
)

func TestSpan(t *testing.T) {
    tests := []struct {
        name     string
        dst      []float64
        l, u     float64
        expected []float64
        shouldPanic bool
    }{
        {
            name:     "Basic Span",
            dst:      make([]float64, 5),
            l:        0,
            u:        4,
            expected: []float64{0, 1, 2, 3, 4},
            shouldPanic: false,
        },
        {
            name:     "Panic on length less than 2",
            dst:      make([]float64, 1),
            l:        0,
            u:        4,
            expected: nil,
            shouldPanic: true,
        },
        {
            name:     "Span with NaN start",
            dst:      make([]float64, 5),
            l:        math.NaN(),
            u:        4,
            expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 4},
            shouldPanic: false,
        },
        {
            name:     "Span with NaN end",
            dst:      make([]float64, 5),
            l:        0,
            u:        math.NaN(),
            expected: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
            shouldPanic: false,
        },
        {
            name:     "Span with infinite bounds",
            dst:      make([]float64, 5),
            l:        math.Inf(-1),
            u:        math.Inf(1),
            expected: []float64{math.Inf(-1), math.Inf(-1), 0, math.Inf(1), math.Inf(1)},
            shouldPanic: false,
        },
        {
            name:     "Span where l equals u",
            dst:      make([]float64, 5),
            l:        2,
            u:        2,
            expected: []float64{2, 2, 2, 2, 2},
            shouldPanic: false,
        },
    }

    for _, tt := range tests {
        t.Run(tt.name, func(t *testing.T) {
            if tt.shouldPanic {
                defer func() {
                    if r := recover(); r == nil {
                        t.Errorf("expected panic but did not occur")
                    }
                }()
            }

            result := Span(tt.dst, tt.l, tt.u)

            if !equalSlices(result, tt.expected) {
                t.Errorf("expected %v, got %v", tt.expected, result)
            }
        })
    }
}

func equalSlices(a, b []float64) bool {
    if len(a) != len(b) {
        return false
    }
    for i := range a {
        if !float64Equal(a[i], b[i]) {
            return false
        }
    }
    return true
}

func float64Equal(a, b float64) bool {
    return (math.IsNaN(a) && math.IsNaN(b)) || (a == b)
}

