package floats

import (
    "math"
    "testing"
)

func TestNorm(t *testing.T) {
    tests := []struct {
        s       []float64
        L       float64
        expected float64
    }{
        // Test case for L = 1
        {[]float64{1, 2, -3}, 1, 6},
        {[]float64{}, 1, 0}, // edge case: empty slice

        // Test case for L = 2
        {[]float64{1, 2, -3}, 2, math.Sqrt(14)},

        // Test case for L = math.Inf(1)
        {[]float64{1, 2, -3}, math.Inf(1), 3},

        // Test case for general L > 0
        {[]float64{1, 2, 3}, 3, math.Pow(36, 1.0/3.0)},

        // Test case for negative values
        {[]float64{-1, -2, -3}, 1, 6},

        // Test case for mixed values
        {[]float64{-1, 0, 1}, 2, 1.4142135623730951}, // sqrt(2)

        // Test case for large values
        {[]float64{1e10, 1e10, 1e10}, 1, 3e10},
    }

    for _, test := range tests {
        result := Norm(test.s, test.L)
        if !almostEqual(result, test.expected) {
            t.Errorf("Norm(%v, %v) = %v; want %v", test.s, test.L, result, test.expected)
        }
    }
}

// Helper function to check for floating point equality
func almostEqual(a, b float64) bool {
    const epsilon = 1e-9
    return math.Abs(a-b) < epsilon
}

