func TestFind(t *testing.T) {
	tests := []struct {
		name     string
		inds     []int
		f        func(float64) bool
		s        []float64
		k        int
		expected []int
		err      error
	}{
		{
			name:     "no elements requested",
			inds:     []int{0, 0, 0},
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{1, 2, 3},
			k:        0,
			expected: []int{},
			err:      nil,
		},
		{
			name:     "find first k elements",
			inds:     []int{0, 0, 0},
			f:        func(x float64) bool { return x > 1 },
			s:        []float64{1, 2, 3, 0, 5},
			k:        2,
			expected: []int{1, 2},
			err:      nil,
		},
		{
			name:     "k less than available elements",
			inds:     []int{0, 0, 0},
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{0, 1, 2, 3},
			k:        2,
			expected: []int{1, 2},
			err:      nil,
		},
		{
			name:     "k greater than available elements",
			inds:     []int{0, 0, 0},
			f:        func(x float64) bool { return x > 5 },
			s:        []float64{1, 2, 3, 4},
			k:        1,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
		{
			name:     "k negative, return all",
			inds:     []int{0, 0, 0},
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{1, 0, -1, 2, 3},
			k:        -1,
			expected: []int{0, 3, 4},
			err:      nil,
		},
		{
			name:     "no valid elements",
			inds:     []int{0, 0, 0},
			f:        func(x float64) bool { return x < 0 },
			s:        []float64{1, 2, 3},
			k:        1,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := Find(tt.inds, tt.f, tt.s, tt.k)
			if !reflect.DeepEqual(got, tt.expected) || !reflect.DeepEqual(err, tt.err) {
				t.Errorf("Find() = %v, %v; expected %v, %v", got, err, tt.expected, tt.err)
			}
		})
	}
}

