package floats

import (
	"math"
	"testing"
)

func TestDistance(t *testing.T) {
	tests := []struct {
		name     string
		s        []float64
		t        []float64
		L        float64
		expected float64
	}{
		{
			name:     "L1 norm",
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			L:        1,
			expected: 9,
		},
		{
			name:     "L2 norm",
			s:        []float64{1, 2},
			t:        []float64{2, 2},
			L:        2,
			expected: math.Sqrt(2),
		},
		{
			name:     "L-infinity norm",
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			L:        math.Inf(1),
			expected: 3,
		},
		{
			name:     "General L-norm with L=3",
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			L:        3,
			expected: math.Pow(9, 1.0/3.0), // (9^(1/3))
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := Distance(tt.s, tt.t, tt.L)
			if !floatsEqual(result, tt.expected) {
				t.Errorf("Distance(%v, %v, %v) = %v; want %v", tt.s, tt.t, tt.L, result, tt.expected)
			}
		})
	}

	t.Run("Panic on length mismatch", func(t *testing.T) {
		defer func() {
			if r := recover(); r == nil {
				t.Errorf("Distance should panic on length mismatch")
			}
		}()
		_ = Distance([]float64{1}, []float64{1, 2}, 1)
	})
}

func floatsEqual(a, b float64) bool {
	return math.Abs(a-b) < 1e-9 // Adjust tolerance as needed
}

