package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		name     string
		dst      []float64
		l        float64
		u        float64
		expected []float64
	}{
		{
			name:     "Basic span",
			dst:      make([]float64, 5),
			l:        0,
			u:        1,
			expected: []float64{0, 0.25, 0.5, 0.75, 1},
		},
		{
			name:     "Negative span",
			dst:      make([]float64, 3),
			l:        -1,
			u:        1,
			expected: []float64{-1, 0, 1},
		},
		{
			name:     "Single element span",
			dst:      make([]float64, 2),
			l:        2,
			u:        4,
			expected: []float64{2, 4},
		},
		{
			name:     "Panics when dst length < 2",
			dst:      make([]float64, 1),
			l:        0,
			u:        1,
			expected: nil,
		},
		{
			name:     "NaN lower bound",
			dst:      make([]float64, 5),
			l:        math.NaN(),
			u:        1,
			expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 1},
		},
		{
			name:     "Infinity lower bound",
			dst:      make([]float64, 4),
			l:        math.Inf(-1),
			u:        0,
			expected: []float64{math.Inf(-1), math.Inf(-1), math.Inf(-1), 0},
		},
		{
			name:     "Infinity upper bound",
			dst:      make([]float64, 4),
			l:        0,
			u:        math.Inf(1),
			expected: []float64{0, 0, math.Inf(1), math.Inf(1)},
		},
		{
			name:     "Both bounds infinity",
			dst:      make([]float64, 5),
			l:        math.Inf(-1),
			u:        math.Inf(1),
			expected: []float64{math.Inf(-1), math.Inf(-1), 0, math.Inf(1), math.Inf(1)},
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.expected == nil {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Span() did not panic for length < 2")
					}
				}()
			}
			result := Span(tt.dst, tt.l, tt.u)
			if !equalSlices(result, tt.expected) {
				t.Errorf("Span() = %v, want %v", result, tt.expected)
			}
		})
	}
}

func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if math.IsNaN(a[i]) && math.IsNaN(b[i]) {
			continue
		}
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

