		{[]float64{}, 2, 0},
		// Case: L = 0 (should not compute zero norm, use Count instead)
		{[]float64{1, 2, 3}, 0, math.NaN()},
	}

	for _, test := range tests {
		result := Norm(test.slice, test.L)
		if math.IsNaN(test.expected) && !math.IsNaN(result) {
			t.Errorf("Expected NaN but got %v for input %v and L=%v", result, test.slice, test.L)
		} else if result != test.expected {
			t.Errorf("Norm(%v, %v) = %v; want %v", test.slice, test.L, result, test.expected)
		}
	}
