package floats

import (
	"math"
	"testing"
)

func TestLogSumExp(t *testing.T) {
	tests := []struct {
		input    []float64
		expected float64
	}{
		{
			input:    []float64{1.0, 2.0, 3.0},
			expected: math.Log(math.Exp(1.0) + math.Exp(2.0) + math.Exp(3.0)),
		},
		{
			input:    []float64{0.0, 0.0, 0.0},
			expected: 0.0, // ln(3) since e^0 + e^0 + e^0 = 3
		},
		{
			input:    []float64{-1.0, -2.0, -3.0},
			expected: math.Log(math.Exp(-1.0) + math.Exp(-2.0) + math.Exp(-3.0)),
		},
		{
			input:    []float64{math.Log(math.MaxFloat64), 0},
			expected: math.Log(math.Exp(math.Log(math.MaxFloat64)) + math.Exp(0)), // should not overflow
		},
		{
			input:    []float64{math.Inf(1), 0},
			expected: math.Inf(1), // The result should be +Inf
		},
		{
			input:    []float64{math.Inf(-1), math.Inf(-1)},
			expected: math.Inf(-1), // The result should be -Inf
		},
	}

	for _, tt := range tests {
		t.Run(fmt.Sprintf("%v", tt.input), func(t *testing.T) {
			result := LogSumExp(tt.input)
			if !math.IsNaN(tt.expected) {
				if math.Abs(result-tt.expected) > 1e-9 {
					t.Errorf("expected %v, got %v", tt.expected, result)
				}
			} else if !math.IsNaN(result) {
				t.Errorf("expected NaN, got %v", result)
			}
		})
	}

	// Test panic for empty slice
	defer func() {
		if recover() == nil {
			t.Errorf("Expected panic when passing an empty slice")
		}
	}()
	LogSumExp([]float64{})
}

