package floats

import (
	"testing"
)

func TestEqualApprox(t *testing.T) {
	tests := []struct {
		name     string
		s1      []float64
		s2      []float64
		tol     float64
		expected bool
	}{
		{
			name:     "Equal slices with zero tolerance",
			s1:      []float64{1.0, 2.0, 3.0},
			s2:      []float64{1.0, 2.0, 3.0},
			tol:     0.0,
			expected: true,
		},
		{
			name:     "Equal slices within tolerance",
			s1:      []float64{1.0, 2.0, 3.0},
			s2:      []float64{1.0, 2.0001, 3.0},
			tol:     0.001,
			expected: true,
		},
		{
			name:     "Not equal slices outside tolerance",
			s1:      []float64{1.0, 2.0, 3.0},
			s2:      []float64{1.0, 2.5, 3.0},
			tol:     0.1,
			expected: false,
		},
		{
			name:     "Different lengths",
			s1:      []float64{1.0, 2.0},
			s2:      []float64{1.0, 2.0, 3.0},
			tol:     0.1,
			expected: false,
		},
		{
			name:     "Equal slices with relative tolerance",
			s1:      []float64{1.0, 1.0, 1.0},
			s2:      []float64{1.0, 1.01, 1.0},
			tol:     0.02,
			expected: true,
		},
		{
			name:     "Equal slices with negative values",
			s1:      []float64{-1.0, -2.0, -3.0},
			s2:      []float64{-1.0, -2.0001, -3.0},
			tol:     0.001,
			expected: true,
		},
		{
			name:     "Not equal slices with negative values",
			s1:      []float64{-1.0, -2.0, -3.0},
			s2:      []float64{-1.0, -2.5, -3.0},
			tol:     0.1,
			expected: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := EqualApprox(tt.s1, tt.s2, tt.tol)
			if result != tt.expected {
				t.Errorf("EqualApprox(%v, %v, %v) = %v; want %v", tt.s1, tt.s2, tt.tol, result, tt.expected)
			}
		})
	}
}

