func TestDistance(t *testing.T) {
	tests := []struct {
		s       []float64
		t       []float64
		L       float64
		expected float64
		expectPanic bool
	}{
		// Test with L1 norm
		{s: []float64{1.0, 2.0, 3.0}, t: []float64{1.0, 2.0, 3.0}, L: 1.0, expected: 0.0, expectPanic: false},
		{s: []float64{1.0, 2.0, 3.0}, t: []float64{4.0, 5.0, 6.0}, L: 1.0, expected: 9.0, expectPanic: false},

		// Test with L2 norm
		{s: []float64{1.0, 2.0, 3.0}, t: []float64{1.0, 2.0, 3.0}, L: 2.0, expected: 0.0, expectPanic: false},
		{s: []float64{1.0, 2.0, 3.0}, t: []float64{4.0, 5.0, 6.0}, L: 2.0, expected: math.Sqrt(27), expectPanic: false},

		// Test with L-infinity norm
		{s: []float64{1.0, 2.0, 3.0}, t: []float64{4.0, 5.0, 6.0}, L: math.Inf(1), expected: 3.0, expectPanic: false},

		// Test with L-norm for L=3
		{s: []float64{1.0, 2.0, 3.0}, t: []float64{4.0, 5.0, 6.0}, L: 3.0, expected: math.Pow(27, 1.0/3.0), expectPanic: false},

		// Test panic cases
		{s: []float64{1.0, 2.0}, t: []float64{1.0}, L: 2.0, expected: 0.0, expectPanic: true},  // different lengths
		{s: []float64{}, t: []float64{}, L: 1.0, expected: 0.0, expectPanic: false},  // both slices empty
	}

	for _, test := range tests {
		if test.expectPanic {
			func() {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Expected panic for s: %v, t: %v, L: %v", test.s, test.t, test.L)
					}
				}()
				Distance(test.s, test.t, test.L)
			}()
		} else {
			result := Distance(test.s, test.t, test.L)
			if result != test.expected {
				t.Errorf("Distance(%v, %v, %v) = %v; expected %v", test.s, test.t, test.L, result, test.expected)
			}
		}
	}
}

