func TestNearestIdx(t *testing.T) {
	tests := []struct {
		s       []float64
		v       float64
		wantIdx int
	}{
		{[]float64{1.0, 2.0, 3.0}, 2.5, 1},  // First nearest is 2.0
		{[]float64{1.0, 2.0, 3.0, 3.0}, 3.1, 2}, // Among two 3s, return the first
		{[]float64{1.0, 2.0, 3.0}, 3.0, 2},  // Exact match at the end
		{[]float64{5.0, 10.0}, 7.5, 0},      // Nearest is 5.0
		{[]float64{1.0, 2.0, 3.0, 4.0}, 1.5, 0}, // Nearest is 1.0
		{[]float64{1.0, 1.0, 1.0}, 1.0, 0},  // Multiple same values, expect first index
		{[]float64{1.0, 2.0, 2.5}, 2.4, 1},  // Between 2 and 2.5, expect 2.0
		{[]float64{math.NaN(), 3.0, 4.0}, 3.1, 1}, // NaN should be ignored
		{[]float64{1.0, 2.0, 3.0}, math.NaN(), 0}, // NaN for v should return 1 not 0
		{[]float64{1.0, 2.0, 3.0}, math.Inf(1), 2}, // +Inf expects last index
		{[]float64{1.0, 2.0, 3.0}, math.Inf(-1), 0}, // -Inf expects first index
	}

	for _, tt := range tests {
		t.Run("", func(t *testing.T) {
			if got := NearestIdx(tt.s, tt.v); got != tt.wantIdx {
				t.Errorf("NearestIdx(%v, %v) = %d; want %d", tt.s, tt.v, got, tt.wantIdx)
			}
		})
	}
}

