func TestSpan(t *testing.T) {
	t.Run("Normal case", func(t *testing.T) {
		dst := make([]float64, 5)
		l, u := 0.0, 10.0
		result := Span(dst, l, u)

		expected := []float64{0, 2.5, 5, 7.5, 10}
		for i, v := range expected {
			if result[i] != v {
				t.Errorf("Expected %v at index %d, got %v", v, i, result[i])
			}
		}
	})

	t.Run("Panic on short length", func(t *testing.T) {
		defer func() {
			if recover() == nil {
				t.Errorf("Expected panic for length less than 2")
			}
		}()
		Span(make([]float64, 1), 0, 10)
	})

	t.Run("NaN lower bound", func(t *testing.T) {
		dst := make([]float64, 5)
		result := Span(dst, math.NaN(), 10.0)

		expected := []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10.0}
		for i, v := range expected {
			if (math.IsNaN(result[i]) && !math.IsNaN(v)) || result[i] != v {
				t.Errorf("Expected %v at index %d, got %v", v, i, result[i])
			}
		}
	})

	t.Run("NaN upper bound", func(t *testing.T) {
		dst := make([]float64, 5)
		result := Span(dst, 0.0, math.NaN())

		expected := []float64{0.0, math.NaN(), math.NaN(), math.NaN(), math.NaN()}
		for i, v := range expected {
			if (math.IsNaN(result[i]) && !math.IsNaN(v)) || result[i] != v {
				t.Errorf("Expected %v at index %d, got %v", v, i, result[i])
			}
		}
	})

	t.Run("Positive infinity lower bound", func(t *testing.T) {
		dst := make([]float64, 5)
		result := Span(dst, math.Inf(1), math.Inf(-1))

		expected := []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(-1)}
		for i, v := range expected {
			if result[i] != v {
				t.Errorf("Expected %v at index %d, got %v", v, i, result[i])
			}
		}
	})

	t.Run("Negative infinity upper bound", func(t *testing.T) {
		dst := make([]float64, 5)
		result := Span(dst, math.Inf(-1), math.Inf(0))

		expected := []float64{math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(0)}
		for i, v := range expected {
			if result[i] != v {
				t.Errorf("Expected %v at index %d, got %v", v, i, result[i])
			}
		}
	})
}

