package floats

import (
	"math"
	"testing"
)

func TestNorm(t *testing.T) {
	tests := []struct {
		input    []float64
		L        float64
		expected float64
	}{
		// Test L = 1
		{[]float64{1, -2, 3}, 1, 6},
		{[]float64{-1, -2, -3}, 1, 6},
		{[]float64{}, 1, 0}, // empty slice

		// Test L = 2
		{[]float64{3, 4}, 2, 5}, // 3-4-5 triangle

		// Test L = math.Inf(1)
		{[]float64{1, -2, 3}, math.Inf(1), 3},
		{[]float64{-1, -5, 2}, math.Inf(1), 5},

		// Test L > 2
		{[]float64{1, 2, 3}, 3, math.Pow(14, 1.0/3.0)}, // (1^3 + 2^3 + 3^3)^(1/3)

		// Test with zeros
		{[]float64{0, 0, 0}, 1, 0},
		{[]float64{0, 0, 0}, 2, 0},
	}

	for _, test := range tests {
		result := Norm(test.input, test.L)
		if !floatEquals(result, test.expected) {
			t.Errorf("Norm(%v, %v) = %v; want %v", test.input, test.L, result, test.expected)
		}
	}
}

func floatEquals(a, b float64) bool {
	const epsilon = 1e-9
	return math.Abs(a-b) < epsilon
}

