package floats

import (
	"errors"
	"testing"
)

func TestFind(t *testing.T) {
	tests := []struct {
		name        string
		inds        []int
		f           func(float64) bool
		s           []float64
		k           int
		expected    []int
		expectError bool
	}{
		{
			name:        "No elements found",
			inds:        nil,
			f:           func(x float64) bool { return x > 10 },
			s:           []float64{1, 2, 3, 4},
			k:           2,
			expected:    []int{},
			expectError: true,
		},
		{
			name: "One element found",
			inds: nil,
			f:    func(x float64) bool { return x == 2 },
			s:    []float64{1, 2, 3, 4},
			k:    1,
			expected: []int{
				1,
			},
			expectError: false,
		},
		{
			name: "Multiple elements found, limit is satisfied",
			inds: nil,
			f:    func(x float64) bool { return x > 1 },
			s:    []float64{1, 2, 3, 4},
			k:    2,
			expected: []int{
				1, 2,
			},
			expectError: false,
		},
		{
			name: "More results than expected",
			inds: nil,
			f:    func(x float64) bool { return x > 1 },
			s:    []float64{1, 2, 3, 4},
			k:    1,
			expected: []int{
				1,
			},
			expectError: false,
		},
		{
			name:        "Request all elements with k < 0",
			inds:        nil,
			f:           func(x float64) bool { return x > 0 },
			s:           []float64{1, 2, 3, 4},
			k:           -1,
			expected:    []int{0, 1, 2, 3},
			expectError: false,
		},
		{
			name: "Insufficient elements found with positive k",
			inds: nil,
			f:    func(x float64) bool { return x > 5 },
			s:    []float64{1, 2, 3, 4},
			k:    2,
			expected: []int{},
			expectError: true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result, err := Find(tt.inds, tt.f, tt.s, tt.k)

			if (err != nil) != tt.expectError {
				t.Errorf("expected error: %v, got: %v", tt.expectError, err)
			}

			if len(result) != len(tt.expected) {
				t.Errorf("expected length: %d, got: %d", len(tt.expected), len(result))
			}

			for i := range result {
				if result[i] != tt.expected[i] {
					t.Errorf("expected: %v, got: %v", tt.expected, result)
					break
				}
			}
		})
	}
}

