func TestDistance(t *testing.T) {
	tests := []struct {
		s        []float64
		t        []float64
		L        float64
		expected float64
		hasPanic bool
	}{
		// Test cases for L1 norm
		{[]float64{1.0, 2.0, 3.0}, []float64{4.0, 5.0, 6.0}, 1.0, 9.0, false},
		{[]float64{-1.0, -2.0}, []float64{-1.0, -5.0}, 1.0, 3.0, false},

		// Test cases for L2 norm
		{[]float64{3.0, 4.0}, []float64{0.0, 0.0}, 2.0, 5.0, false},
		{[]float64{1.0}, []float64{1.0}, 2.0, 0.0, false},

		// Test cases for infinity norm
		{[]float64{1.0, -2.0, 3.0}, []float64{4.0, 5.0, 6.0}, math.Inf(1), 3.0, false},

		// Test cases for arbitrary L norms
		{[]float64{1.0, 2.0}, []float64{2.0, 3.0}, 3.0, math.Pow(1+1, 1/3.0), false},

		// Test case for mismatched lengths (should panic)
		{[]float64{1.0, 2.0}, []float64{1.0}, 2.0, 0.0, true},
	}

	for _, test := range tests {
