func TestNearestIdxForSpan(t *testing.T) {
	tests := []struct {
		n, expected int
		l, u, v     float64
		panic       bool
	}{
		{5, 2, 0, 10, 5, false}, // Middle value
		{5, 0, 0, 10, -1, false}, // Below lower bound
		{5, 4, 0, 10, 11, false}, // Above upper bound
		{5, 2, 0, 10, 5.1, false}, // Close to middle value
		{3, 1, 0, 10, 3, false}, // Between bounds
		{5, 0, 0, 0, 0, false}, // Lower bound equals value
		{5, 4, 10, 10, 10, false}, // Upper bound equals value
		{5, 0, math.Inf(-1), math.Inf(1), 0, false}, // Inf limits
		{5, 0, math.Inf(-1), math.Inf(1), math.Inf(-1), false}, // v is negative Inf
		{5, 4, math.Inf(-1), math.Inf(1), math.Inf(1), false}, // v is positive Inf
		{5, 2, 10, 0, 5, false}, // Bounds reversed
		{2, 0, 0, 10, 5, false}, // Edge case with n=2
		{1, 0, 0, 10, 5, true}, // Panic case n < 2
	}

	for _, test := range tests {
		if test.panic {
			defer func() { recover() }()
			NearestIdxForSpan(test.n, test.l, test.u, test.v)
			t.Errorf("Expected panic for n=%d, l=%f, u=%f, v=%f but did not panic", test.n, test.l, test.u, test.v)
