import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	t.Run("Valid span", func(t *testing.T) {
		dst := make([]float64, 5)
		expected := []float64{1, 2, 3, 4, 5}
		result := Span(dst, 1, 5)

		for i, v := range result {
			if v != expected[i] {
				t.Errorf("Expected %v, got %v", expected[i], v)
			}
		}
	})

	t.Run("Empty slice panics", func(t *testing.T) {
		defer func() {
			if r := recover(); r == nil {
				t.Errorf("Expected panic, got none")
			}
		}()
		Span(make([]float64, 1), 1, 5)
	})

	t.Run("NaN lower bound", func(t *testing.T) {
		dst := make([]float64, 5)
		expected := []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 5}
		result := Span(dst, math.NaN(), 5)

		for i, v := range result {
			if math.IsNaN(v) && !math.IsNaN(expected[i]) {
				t.Errorf("Expected %v, got NaN", expected[i])
			} else if !math.IsNaN(v) && math.IsNaN(expected[i]) {
				t.Errorf("Expected NaN, got %v", v)
			}
		}
	})

	t.Run("NaN upper bound", func(t *testing.T) {
		dst := make([]float64, 5)
		expected := []float64{1, 2, 3, 4, math.NaN()}
		result := Span(dst, 1, math.NaN())

		for i, v := range result {
			if math.IsNaN(v) && !math.IsNaN(expected[i]) {
				t.Errorf("Expected %v, got NaN", expected[i])
			} else if !math.IsNaN(v) && math.IsNaN(expected[i]) {
				t.Errorf("Expected NaN, got %v", v)
			}
		}
	})

	t.Run("Infinite lower bound", func(t *testing.T) {
		dst := make([]float64, 5)
		expected := []float64{math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(-1), 5}
		result := Span(dst, math.Inf(-1), 5)

		for i, v := range result {
			if v != expected[i] {
				t.Errorf("Expected %v, got %v", expected[i], v)
			}
		}
	})

	t.Run("Infinite upper bound", func(t *testing.T) {
		dst := make([]float64, 5)
		expected := []float64{1, 2, 3, 4, math.Inf(1)}
		result := Span(dst, 1, math.Inf(1))

		for i, v := range result {
			if v != expected[i] {
				t.Errorf("Expected %v, got %v", expected[i], v)
			}
		}
	})

	t.Run("Equal bounds", func(t *testing.T) {
		dst := make([]float64, 5)
		expected := []float64{3, 3, 3, 3, 3}
		result := Span(dst, 3, 3)

		for i, v := range result {
			if v != expected[i] {
				t.Errorf("Expected %v, got %v", expected[i], v)
			}
		}
	})
}

