package floats

import (
	"math"
	"testing"
)

func TestDistance(t *testing.T) {
	tests := []struct {
		s     []float64
		t     []float64
		L     float64
		want  float64
		hasPanic bool
	}{
		{
			s:     []float64{1, 2, 3},
			t:     []float64{4, 5, 6},
			L:     1,
			want:  9,
			hasPanic: false,
		},
		{
			s:     []float64{1, 2, 3},
			t:     []float64{4, 5, 6},
			L:     2,
			want:  5.196152422706632, // expected L2 distance
			hasPanic: false,
		},
		{
			s:     []float64{1, 2, 3},
			t:     []float64{1, 2, 3},
			L:     math.Inf(1),
			want:  0,
			hasPanic: false,
		},
		{
			s:     []float64{1, 2, 3},
			t:     []float64{4, 5},
			L:     1,
			want:  0,
			hasPanic: true, // Expecting panic due to mismatched lengths
		},
		{
			s:     []float64{},
			t:     []float64{},
			L:     1,
			want:  0,
			hasPanic: false,
		},
		{
			s:     []float64{5, 9, 2},
			t:     []float64{1, 3, 5},
			L:     3,
			want:  4.641588834,
			hasPanic: false,
		},
	}

	for _, tt := range tests {
		if tt.hasPanic {
			t.Run("Panic", func(t *testing.T) {
				defer func() {
					if recover() == nil {
						t.Errorf("Expected panic for input %v and %v, but did not panic", tt.s, tt.t)
					}
				}()
				Distance(tt.s, tt.t, tt.L)
			})
		} else {
			t.Run("No Panic", func(t *testing.T) {
				got := Distance(tt.s, tt.t, tt.L)
				if !almostEqual(got, tt.want) {
					t.Errorf("Distance(%v, %v, %v) = %v; want %v", tt.s, tt.t, tt.L, got, tt.want)
				}
			})
		}
	}
}

// Helper function to check for approximate equality of floats
func almostEqual(a, b float64) bool {
	const epsilon = 1e-9
	return math.Abs(a-b) < epsilon
}

