        s         []float64
        v         float64
        expected  int
        shouldPanic bool
    }{
        {[]float64{1.0, 2.0, 3.0}, 2.5, 1, false},  // Nearest to 2.5 is 2.0 at index 1
        {[]float64{1.0, 2.0, 3.0}, 1.5, 0, false},  // Nearest to 1.5 is 1.0 at index 0
        {[]float64{1.0, 2.0, 3.0}, 3.5, 2, false},  // Nearest to 3.5 is 3.0 at index 2
