        {[]float64{-1.0, -2.0, -3.0}, -1.5, 0, false}, // Nearest to -1.5 is -1.0 at index 0
        {[]float64{}, 1.0, -1, true},                // Should panic due to zero length
        {[]float64{math.NaN()}, math.NaN(), 0, false}, // Nearest to NaN returns index 0
        {[]float64{1.0, 2.0, math.Inf(1)}, math.Inf(1), 2, false}, // Nearest to positive infinity returns index of max
        {[]float64{1.0, 2.0, math.Inf(-1)}, math.Inf(-1), 0, false}, // Nearest to negative infinity returns index of min
    }

    for _, test := range tests {
        if test.shouldPanic {
            defer func() {
                if r := recover(); r == nil {
                    t.Errorf("Expected panic for input %v and %v, but did not panic", test.s, test.v)
                }
            }()
        }

        result := NearestIdx(test.s, test.v)
        if !test.shouldPanic && result != test.expected {
            t.Errorf("NearestIdx(%v, %v) = %d; expected %d", test.s, test.v, result, test.expected)
        }
    }
}

