func TestNearestIdx(t *testing.T) {
	tests := []struct {
		s      []float64
		v      float64
		Expect int
	}{
		// Test with normal values
		{[]float64{1.0, 2.0, 3.0}, 2.5, 1},  // Nearest to 2.5 is 2.0 at index 1
		{[]float64{1.0, 1.5, 2.5}, 1.1, 1},  // Nearest to 1.1 is 1.0 at index 0
		{[]float64{1.0, 2.1, 3.0}, 2.0, 1},  // Nearest to 2.0 is 2.1 at index 1

		// Test with edge cases
		{[]float64{1.0, 2.0, 3.0}, 3.0, 2},  // Nearest to 3.0 is 3.0 at index 2
		{[]float64{1.0, 1.0, 1.0}, 1.0, 0},  // Nearest to 1.0, first occurrence at index 0
		{[]float64{1.0, 2.0, 3.0}, 0.0, 0},  // Nearest to 0.0 is 1.0 at index 0

		// Test with NaN case
		{[]float64{1.0, 2.0, 3.0}, math.NaN(), 0}, // Nearest NaN returns first index

		// Test with Inf values
		{[]float64{1.0, 2.0, 3.0}, math.Inf(1), 2}, // Nearest positive inf returns index of max value
		{[]float64{1.0, 2.0, 3.0}, math.Inf(-1), 0}, // Nearest negative inf returns index of min value

		// Test for zero length slice (should panic)
	}

	for _, test := range tests {
		if test.v == math.NaN() { // Special handling for NaN test case
			continue
		}

		t.Run("", func(t *testing.T) {
			defer func() {
				if r := recover(); r != nil {
					if test.s == nil || len(test.s) != 0 {
						t.Fatalf("Expected not to panic for input: %v", test)
					}
				}
			}()

			got := NearestIdx(test.s, test.v)
			if got != test.Expect {
				t.Errorf("NearestIdx(%v, %v) = %d; want %d", test.s, test.v, got, test.Expect)
			}
		})
	}

	// Test case for zero length slice
	if !t.Failed() {
		defer func() {
			if r := recover(); r == nil {
				t.Errorf("Expected panic for empty slice")
			}
		}()
		NearestIdx([]float64{}, 1.0)
	}
}

