func TestSpan(t *testing.T) {
	tests := []struct {
		name     string
		dst      []float64
		l, u     float64
		expected []float64
		expectPanic bool
	}{
		{
			name:     "Valid range",
			dst:      make([]float64, 5),
			l:        0,
			u:        4,
			expected: []float64{0, 1, 2, 3, 4},
			expectPanic: false,
		},
		{
			name:     "Panic for length less than 2",
			dst:      make([]float64, 1),
			l:        0,
			u:        4,
			expected: nil,
			expectPanic: true,
		},
		{
			name:     "NaN lower bound",
			dst:      make([]float64, 5),
			l:        math.NaN(),
			u:        4,
			expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 4},
			expectPanic: false,
		},
		{
			name:     "NaN upper bound",
			dst:      make([]float64, 5),
			l:        0,
			u:        math.NaN(),
			expected: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
			expectPanic: false,
		},
		{
			name:     "Inf lower bound",
			dst:      make([]float64, 5),
			l:        math.Inf(-1),
			u:        math.Inf(1),
			expected: []float64{math.Inf(-1), math.Inf(-1), 0, math.Inf(1), math.Inf(1)},
			expectPanic: false,
		},
		{
			name:     "Inf upper bound",
			dst:      make([]float64, 5),
			l:        0,
			u:        math.Inf(1),
			expected: []float64{0, 0, 0, 0, math.Inf(1)},
			expectPanic: false,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.expectPanic {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Expected panic but did not panic.")
					}
				}()
			}
			result := Span(tt.dst, tt.l, tt.u)
			if !tt.expectPanic && !equalSlices(result, tt.expected) {
				t.Errorf("Expected %v, but got %v", tt.expected, result)
			}
		})
	}
}

func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if math.IsNaN(a[i]) && math.IsNaN(b[i]) {
			continue
		}
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

