func TestNearestIdxForSpan(t *testing.T) {
	tests := []struct {
		n      int
		l      float64
		u      float64
		v      float64
		expect int
	}{
		{5, 0, 10, 5, 2},               // middle value
		{5, 0, 10, 9, 4},               // close to upper bound
		{5, 0, 10, 1, 1},               // close to lower bound
		{5, 0, 10, -1, 0},              // below lower bound
		{5, 0, 10, 11, 4},              // above upper bound
		{3, -5, 5, 0, 1},               // middle value with negative bounds
		{3, -5, 5, -6, 0},              // below lower with negative bounds
		{3, -5, 5, 6, 2},               // above upper with negative bounds
		{4, 9, 10, 9.5, 1},             // even count of points
		{2, -1, 1, 0, 0},               // minimum valid points
		{2, -1, 1, -1, 0},              // equal to lower bound
		{2, -1, 1, 1, 1},               // equal to upper bound
		{3, math.Inf(-1), math.Inf(1), 0, 1}, // Inf case
		{3, math.Inf(1), math.Inf(1), 0, 0},  // Inf case
		{3, -1, math.Inf(1), 0, 0},    // Inf case with -1 as low
	}

	for _, test := range tests {
		t.Run("", func(t *testing.T) {
			defer func() {
				if recover() == nil {
					t.Fatalf("Expected panic for n = %d, but did not panic", test.n)
				}
			}()
			if test.n < 2 {
				NearestIdxForSpan(test.n, test.l, test.u, test.v)
			} else {
				got := NearestIdxForSpan(test.n, test.l, test.u, test.v)
