			dst:        []float64{0, 0},
			s:          []float64{1, 2, 3},
			t:          []float64{4, 5, 6},
			expected:   nil,
			expectPanic: true,
		},
		{
			name:       "Different lengths - s and t",
			dst:        []float64{0, 0, 0},
			s:          []float64{1, 2},
			t:          []float64{3, 4, 5},
			expected:   nil,
			expectPanic: true,
		},
