package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		dst       []float64
		l, u     float64
		expected []float64
		expectPanic bool
	}{
		// Normal case
		{dst: make([]float64, 5), l: 0, u: 10, expected: []float64{0, 2.5, 5, 7.5, 10}, expectPanic: false},
		{dst: make([]float64, 3), l: 1, u: 5, expected: []float64{1, 3, 5}, expectPanic: false},

		// Case where dst contains less than 2 elements (should panic)
		{dst: make([]float64, 1), l: 0, u: 1, expectPanic: true},
		{dst: nil, l: 0, u: 1, expectPanic: true}, // nil slice should also panic

		// Special cases for Inf and NaN
		{dst: make([]float64, 5), l: math.Inf(-1), u: math.Inf(1), expected: []float64{math.Inf(-1), math.Inf(-1), 0, math.Inf(1), math.Inf(1)}, expectPanic: false},
		{dst: make([]float64, 5), l: math.NaN(), u: 10, expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10}, expectPanic: false},
		{dst: make([]float64, 5), l: 0, u: math.NaN(), expected: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()}, expectPanic: false},

		// Case where l and u are both NaN (should return all NaN elements)
		{dst: make([]float64, 5), l: math.NaN(), u: math.NaN(), expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), math.NaN()}, expectPanic: false},
	}

	for _, test := range tests {
		if test.expectPanic {
			func() {
				defer func() { recover() }() // recover from panic to test the panic behavior
				Span(test.dst, test.l, test.u)
				t.Errorf("expected panic for input l=%v, u=%v, but did not panic", test.l, test.u)
			}()
		} else {
			result := Span(test.dst, test.l, test.u)
			if !equalSlices(result, test.expected) {
				t.Errorf("unexpected result for input l=%v, u=%v: got %v, want %v", test.l, test.u, result, test.expected)
			}
		}
	}
}

// Helper function to check equality of float64 slices
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] { // Consider using math.Abs(a[i]-b[i]) < tolerance for float comparison in practice.
			return false
		}
	}
	return true
}

