			name:     "NaN in end",
			dst:      make([]float64, 5),
			l:        1,
			u:        math.NaN(),
			expected: []float64{1, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
		},
		{
			name:     "Inf in start",
			dst:      make([]float64, 5),
			l:        math.Inf(1),
			u:        5,
			expected: []float64{math.Inf(1), math.Inf(1), math.Inf(1), math.Inf(1), 5},
