package floats

import (
	"testing"
)

func TestEqualLengths(t *testing.T) {
	// Test case 1: No slices provided
	result := EqualLengths()
	if result != true {
		t.Errorf("Expected true, got %v", result)
	}

	// Test case 2: One empty slice
	result = EqualLengths([]float64{})
	if result != true {
		t.Errorf("Expected true, got %v", result)
	}

	// Test case 3: Multiple empty slices
	result = EqualLengths([]float64{}, []float64{}, []float64{})
	if result != true {
		t.Errorf("Expected true, got %v", result)
	}

	// Test case 4: Different lengths of slices
	result = EqualLengths([]float64{1.0, 2.0}, []float64{1.0}, []float64{1.0, 2.0, 3.0})
	if result != false {
		t.Errorf("Expected false, got %v", result)
	}

	// Test case 5: All slices have the same length
	result = EqualLengths([]float64{1.0, 2.0}, []float64{3.0, 4.0}, []float64{5.0, 6.0})
	if result != true {
		t.Errorf("Expected true, got %v", result)
	}

	// Test case 6: One slice is longer than the others
	result = EqualLengths([]float64{1.0, 2.0}, []float64{3.0, 4.0}, []float64{5.0})
	if result != false {
		t.Errorf("Expected false, got %v", result)
	}
}

