package floats

import (
	"errors"
	"testing"
)

// TestFind tests the Find function.
func TestFind(t *testing.T) {
	tests := []struct {
		name     string
		inds     []int
		f        func(float64) bool
		s        []float64
		k        int
		expected []int
		err      error
	}{
		{
			name:     "No match, k negative",
			inds:     nil,
			f:        func(x float64) bool { return x > 10 },
			s:        []float64{1, 2, 3, 4, 5},
			k:        -1,
			expected: []int{},
			err:      nil,
		},
		{
			name:     "Two matches, k positive",
			inds:     nil,
			f:        func(x float64) bool { return x%2 == 0 },
			s:        []float64{1, 2, 3, 4, 5},
			k:        2,
			expected: []int{1, 3},
			err:      nil,
		},
		{
			name:     "Insufficient matches, k positive",
			inds:     nil,
			f:        func(x float64) bool { return x > 10 },
			s:        []float64{1, 2, 3, 4, 5},
			k:        1,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
		{
			name:     "Exact matches, k matches found",
			inds:     nil,
			f:        func(x float64) bool { return x > 2 },
			s:        []float64{1, 2, 3, 4, 5},
			k:        3,
			expected: []int{2, 3, 4},
			err:      nil,
		},
		{
			name:     "Zero elements requested",
			inds:     nil,
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{1, 2, 3},
			k:        0,
			expected: []int{},
			err:      nil,
		},
		{
			name:     "Multiple matches, k greater than available",
			inds:     nil,
			f:        func(x float64) bool { return x < 5 },
			s:        []float64{1, 2, 3, 6, 7},
			k:        10,
			expected: []int{0, 1, 2},
			err:      errors.New("floats: insufficient elements found"),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			inds, err := Find(tt.inds, tt.f, tt.s, tt.k)
			if err != nil && err.Error() != tt.err.Error() {
				t.Fatalf("expected error %v, got %v", tt.err, err)
			}
			if !equalSlices(inds, tt.expected) {
				t.Errorf("expected %v, got %v", tt.expected, inds)
			}
		})
	}
}

// equalSlices compares two integer slices for equality.
func equalSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

