		{[]float64{1.0, 2.0, 3.0}, 2.5, 1},
		{[]float64{1.0, 2.0, 3.0}, 3.0, -1}, // v == s[len(s)-1], should return -1
		{[]float64{1.0, 2.0, 3.0}, 4.0, -1}, // v > s[len(s)-1], should return -1

		// Test edge cases
		{[]float64{1.0, 3.0, 2.0}, 2.0, -1}, // unsorted slice, should panic
		{[]float64{1.0}, 1.0, -1}, // len(s) < 2, should panic
		{[]float64{1.0, 2.0, 3.0}, math.NaN(), -1}, // v is NaN, should return -1
		{[]float64{math.NaN(), 2.0, 3.0}, 1.0, -1}, // s contains NaN, should not panic, return -1
	}

