package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	tests := []struct {
		name     string
		dst      []float64
		l, u     float64
		expected []float64
	}{
		{
			name:     "Normal case",
			dst:      make([]float64, 5),
			l:        0,
			u:        4,
			expected: []float64{0, 1, 2, 3, 4},
		},
		{
			name:     "Same l and u",
			dst:      make([]float64, 5),
			l:        2,
			u:        2,
			expected: []float64{2, 2, 2, 2, 2},
		},
		{
			name:     "Negative range",
			dst:      make([]float64, 5),
			l:        -4,
			u:        0,
			expected: []float64{-4, -3, -2, -1, 0},
		},
		{
			name:     "NaN l",
			dst:      make([]float64, 5),
			l:        math.NaN(),
			u:        1,
			expected: []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 1},
		},
		{
			name:     "NaN u",
			dst:      make([]float64, 5),
			l:        0,
			u:        math.NaN(),
			expected: []float64{0, math.NaN(), math.NaN(), math.NaN(), math.NaN()},
		},
		{
			name:     "Infinite l and u",
			dst:      make([]float64, 5),
			l:        math.Inf(-1),
			u:        math.Inf(1),
			expected: []float64{math.Inf(-1), math.Inf(-1), 0, math.Inf(1), math.Inf(1)},
		},
		{
			name:     "Length less than 2",
			dst:      make([]float64, 1),
			l:        0,
			u:        1,
			expected: nil, // This will cause a panic
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if tt.expected == nil {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Span did not panic for dst length less than 2")
					}
				}()
			}
			result := Span(tt.dst, tt.l, tt.u)
			if tt.expected != nil && !equalSlices(result, tt.expected) {
				t.Errorf("expected %v, got %v", tt.expected, result)
			}
		})
	}
}

// Helper function to compare two slices for equality
func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if math.IsNaN(a[i]) && math.IsNaN(b[i]) {
			continue
		}
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

