package floats

import (
	"math"
	"testing"
)

func TestNorm(t *testing.T) {
	tests := []struct {
		slice []float64
		L     float64
		want  float64
	}{
		// Test cases for L = 1 ( Manhattan Norm )
		{[]float64{1, 2, 3}, 1, 6},
		{[]float64{-1, -2, -3}, 1, 6},
		{[]float64{1, -2, 3}, 1, 6},
		{[]float64{}, 1, 0},

		// Test cases for L = 2 ( Euclidean Norm )
		{[]float64{3, 4}, 2, 5},               // 3-4-5 triangle
		{[]float64{-3, -4}, 2, 5},             // Same distance in the negative quadrant
		{[]float64{1, 2, 2}, 2, math.Sqrt(9)}, // (1^2 + 2^2 + 2^2)^(1/2) = 3

		// Test case for L = math.Inf(1) (Max Norm)
		{[]float64{1, 2, 3}, math.Inf(1), 3},
		{[]float64{-1, -2, -3}, math.Inf(1), 3},
		{[]float64{1, -2, 3}, math.Inf(1), 3},
		{[]float64{}, math.Inf(1), 0},

		// Test cases for L = 0 (Not applicable but testing behavior)
		{[]float64{1, 2, 3}, 0, 0}, // Expect either panic or return a default value
		{[]float64{}, 0, 0},        // Expect either panic or return a default value

		// Test cases for large L values
		{[]float64{1, 2, 3}, 10, math.Pow(3, 1.0/10)}, // Expect close to the max value raised to 1/L

		// Edge case for negative and positive mix
		{[]float64{-1, 2, -3}, 1, 6},
	}

	for _, tt := range tests {
		got := Norm(tt.slice, tt.L)
		if !floatEquals(got, tt.want) {
			t.Errorf("Norm(%v, %v) = %v; want %v", tt.slice, tt.L, got, tt.want)
		}
	}
}

// Helper function to compare float64 values for equality within a tolerance
func floatEquals(a, b float64) bool {
	const tolerance = 1e-9
	return math.Abs(a-b) <= tolerance
}

