package floats

import (
	"errors"
	"testing"
)

func TestFind(t *testing.T) {
	tests := []struct {
		name     string
		s        []float64
		f        func(float64) bool
		k        int
		expected []int
		err      error
	}{
		{
			name:     "find first k elements matching f",
			s:        []float64{1.0, 2.0, 3.0, 4.0},
			f:        func(x float64) bool { return x > 2.0 },
			k:        2,
			expected: []int{2, 3},
			err:      nil,
		},
		{
			name:     "find all elements matching f when k is negative",
			s:        []float64{1.0, 2.0, 3.0, 4.0},
			f:        func(x float64) bool { return x > 2.0 },
			k:        -1,
			expected: []int{2, 3},
			err:      nil,
		},
		{
			name:     "not enough elements matching f",
			s:        []float64{1.0, 2.0, 3.0},
			f:        func(x float64) bool { return x > 3.0 },
			k:        2,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
		{
			name:     "k is zero",
			s:        []float64{1.0, 2.0, 3.0, 4.0},
			f:        func(x float64) bool { return x > 0.0 },
			k:        0,
			expected: []int{},
			err:      nil,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			inds := []int{}
			got, err := Find(inds, tt.f, tt.s, tt.k)

			if !equalSlices(got, tt.expected) || !errors.Is(err, tt.err) {
				t.Errorf("Find() = %v, %v; want %v, %v", got, err, tt.expected, tt.err)
			}
		})
	}
}

func equalSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

