func TestFind(t *testing.T) {
	tests := []struct {
		inds     []int
		f        func(float64) bool
		s        []float64
		k        int
		expected []int
		err      bool
	}{
		{
			inds:     nil,
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{-1, 0, 1, 2, 3},
			k:        3,
			expected: []int{2, 3, 4}, // Indices of first 3 elements > 0
			err:      false,
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x > 0 },
			s:        []float64{-1, 0, 1, 2, 3},
			k:        5,
			expected: []int{2, 3, 4}, // All matching indices returned
			err:      true,
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x < 0 },
			s:        []float64{-1, -2, -3, -4},
			k:        2,
			expected: []int{0, 1}, // Indices of first 2 elements < 0
			err:      false,
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x < 0 },
			s:        []float64{1, 2, 3},
			k:        1,
			expected: []int{}, // No matching indices
			err:      true,
		},
		{
			inds:     nil,
			f:        func(x float64) bool { return x == 0 },
			s:        []float64{0, 0, 0},
			k:        -1,
			expected: []int{0, 1, 2}, // All matching indices since k < 0
			err:      false,
		},
	}

	for _, tt := range tests {
		inds, err := Find(tt.inds, tt.f, tt.s, tt.k)

		if (err != nil) != tt.err {
			t.Fatalf("expected error: %v, got: %v", tt.err, err)
		}

		if !equalSlices(inds, tt.expected) {
			t.Fatalf("expected: %v, got: %v", tt.expected, inds)
		}
	}
}

func equalSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

