func TestNearestIdx(t *testing.T) {
	tests := []struct {
		name     string
		slice    []float64
		value    float64
		expected int
	}{
		{
			name:     "exact match",
			slice:    []float64{1.0, 2.0, 3.0},
			value:    2.0,
			expected: 1,
		},
		{
			name:     "closest lower",
			slice:    []float64{1.0, 2.0, 3.0},
			value:    1.5,
			expected: 0,
		},
		{
			name:     "closest higher",
			slice:    []float64{1.0, 2.0, 3.0},
			value:    2.5,
			expected: 1,
		},
		{
			name:     "multiple closest",
			slice:    []float64{1.0, 2.0, 2.0, 3.0},
			value:    2.0,
			expected: 1,
		},
		{
			name:     "negative values",
			slice:    []float64{-3.0, -2.0, -1.0},
			value:    -2.5,
			expected: 1,
		},
		{
			name:     "inf value high",
			slice:    []float64{1.0, 2.0, 3.0},
			value:    math.Inf(1),
			expected: 2, // The index of the maximum value
		},
		{
			name:     "inf value low",
			slice:    []float64{1.0, 2.0, 3.0},
			value:    math.Inf(-1),
			expected: 0, // The index of the minimum value
		},
		{
			name:     "nan value",
			slice:    []float64{1.0, 2.0, 3.0},
			value:    math.NaN(),
			expected: 0, // Returns 0 if value is NaN
		},
		{
			name:     "empty slice",
			slice:    []float64{},
			value:    1.0,
			expected: -1, // Expect a panic
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			if len(tt.slice) == 0 {
				defer func() {
					if r := recover(); r == nil {
						t.Errorf("Expected panic for empty slice")
					}
				}()
			}

			got := NearestIdx(tt.slice, tt.value)
			if len(tt.slice) > 0 && got != tt.expected {
				t.Errorf("NearestIdx(%v, %v) = %d; want %d", tt.slice, tt.value, got, tt.expected)
			}
		})
	}
}

