		{5, math.NaN(), 10, 5, 0}, // Upper bound NaN
		{5, 0, math.NaN(), 5, 1},  // Lower bound NaN
		{5, math.NaN(), math.NaN(), 5, 0}, // Both bounds NaN
	}

	for _, tt := range testsNaN {
