package floats

import (
    "math"
    "testing"
)

func TestSpan(t *testing.T) {
    tests := []struct {
        dst     []float64
        l, u    float64
        want    []float64
        shouldPanic bool
    }{
        {
            dst: []float64{0, 0, 0},
            l: 0,
            u: 10,
            want: []float64{0, 5, 10},
            shouldPanic: false,
        },
        {
            dst: []float64{0, 0},
            l: 1,
            u: 1,
            want: []float64{1, 1},
            shouldPanic: false,
        },
        {
            dst: []float64{0, 0, 0, 0},
            l: 1,
            u: 4,
            want: []float64{1, 2, 3, 4},
            shouldPanic: false,
        },
        {
            dst: []float64{0},
            l: 1,
            u: 10,
            shouldPanic: true,
        },
        {
            dst: []float64{},
            l: 0,
            u: 0,
            shouldPanic: true,
        },
        {
            dst: []float64{math.NaN(), 0},
            l: math.NaN(),
            u: 10,
            want: []float64{math.NaN(), 10},
            shouldPanic: false,
        },
        {
            dst: []float64{0, 0},
            l: 0,
            u: math.NaN(),
            want: []float64{0, math.NaN()},
            shouldPanic: false,
        },
        {
            dst: []float64{0, 0},
            l: math.Inf(1),
            u: math.Inf(1),
            want: []float64{math.Inf(1), math.Inf(1)},
            shouldPanic: false,
        },
        {
            dst: []float64{0, 0},
            l: math.Inf(-1),
            u: math.Inf(-1),
            want: []float64{math.Inf(-1), math.Inf(-1)},
            shouldPanic: false,
        },
        {
            dst: []float64{0, 0, 0},
            l: 0,
            u: math.Inf(1),
            want: []float64{0, math.Inf(1), math.Inf(1)},
            shouldPanic: false,
        },
    }

    for _, tt := range tests {
        if tt.shouldPanic {
            func() {
                defer func() {
                    if r := recover(); r == nil {
                        t.Errorf("expected panic for input %v, %v, %v", tt.dst, tt.l, tt.u)
                    }
                }()
                Span(tt.dst, tt.l, tt.u)
            }()
        } else {
            got := Span(tt.dst, tt.l, tt.u)
            if !equal(got, tt.want) {
                t.Errorf("Span(%v, %v, %v) = %v; want %v", tt.dst, tt.l, tt.u, got, tt.want)
            }
        }
    }
}

func equal(a, b []float64) bool {
    if len(a) != len(b) {
        return false
    }
    for i := range a {
        if !floatEqual(a[i], b[i]) {
            return false
        }
    }
    return true
}

func floatEqual(a, b float64) bool {
    return (math.IsNaN(a) && math.IsNaN(b)) || (a == b)
}

