		expected []float64
		inds     []int
		expectedInds []int
	}{
		{
			dst:       []float64{3.0, 1.0, 2.0},
			expected:  []float64{1.0, 2.0, 3.0},
			inds:      make([]int, 3),
			expectedInds: []int{1, 2, 0},
		},
		{
			dst:       []float64{5.5, 3.3, 4.4, 2.2},
			expected:  []float64{2.2, 3.3, 4.4, 5.5},
			inds:      make([]int, 4),
			expectedInds: []int{3, 1, 2, 0},
