// distance_test.go
package floats

import (
	"math"
	"testing"
)

func TestDistance(t *testing.T) {
	tests := []struct {
		name     string
		slice1   []float64
		slice2   []float64
		L        float64
		expected float64
	}{
		{
			name:     "L2 norm with positive numbers",
			slice1:   []float64{1.0, 2.0, 3.0},
			slice2:   []float64{4.0, 5.0, 6.0},
			L:        2,
			expected: math.Sqrt(27), // âˆš((4-1)Â² + (5-2)Â² + (6-3)Â²)
		},
		{
			name:     "L1 norm with positive numbers",
			slice1:   []float64{1.0, 2.0, 3.0},
			slice2:   []float64{4.0, 5.0, 6.0},
			L:        1,
			expected: 9.0, // |4-1| + |5-2| + |6-3|
		},
		{
			name:     "Infinity norm with positive numbers",
			slice1:   []float64{1.0, 2.0, 3.0},
			slice2:   []float64{4.0, 5.0, 6.0},
			L:        math.Inf(1),
			expected: 3.0, // max(|4-1|, |5-2|, |6-3|)
		},
		{
			name:     "L2 norm with negative numbers",
			slice1:   []float64{-1.0, -2.0, -3.0},
			slice2:   []float64{-4.0, -5.0, -6.0},
			L:        2,
			expected: math.Sqrt(27), // same as before
		},
		{
			name:     "L1 norm with mixed numbers",
			slice1:   []float64{-1.0, 2.0, -3.0},
			slice2:   []float64{4.0, -5.0, 6.0},
			L:        1,
			expected: 9.0, // |-1-4| + |2+5| + |-3-6|
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			result := Distance(tt.slice1, tt.slice2, tt.L)
			if !almostEqual(result, tt.expected) {
				t.Errorf("Distance() = %v; want %v", result, tt.expected)
			}
		})
	}

	t.Run("Mismatched lengths", func(t *testing.T) {
		defer func() {
			if recover() == nil {
				t.Error("Expected panic due to mismatched lengths, but did not panic.")
			}
		}()
		Distance([]float64{1, 2, 3}, []float64{1, 2}, 2)
	})
}

// almostEqual checks if two float64 numbers are "close enough" to be considered equal.
func almostEqual(a, b float64) bool {
	const epsilon = 1e-9
	return math.Abs(a-b) < epsilon
}

