		dst := make([]float64, 5)
		result := Span(dst, math.NaN(), 10)
		expected := []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10}
		for i, v := range result {
			if (math.IsNaN(v) && !math.IsNaN(expected[i])) || v != expected[i] {
				t.Errorf("expected %v, got %v", expected[i], v)
			}
		}
