		expected := []float64{math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(-1), math.Inf(1)}
		for i, v := range result {
			if v != expected[i] {
				t.Errorf("expected %v, got %v", expected[i], v)
			}
		}
	})

	t.Run("Infinite upper bound", func(t *testing.T) {
		dst := make([]float64, 5)
		result := Span(dst, 0, math.Inf(1))
		expected := []float64{0, 0, 0, 0, math.Inf(1)}
		for i, v := range result {
