package floats

import (
	"errors"
	"testing"
)

func TestFind(t *testing.T) {
	tests := []struct {
		name     string
		inds     []int
		f        func(float64) bool
		s        []float64
		k        int
		expected []int
		err      error
	}{
		{
			name: "Find first k elements",
			inds: []int{},
			f: func(x float64) bool {
				return x > 2
			},
			s:        []float64{1, 2, 3, 4, 5},
			k:        3,
			expected: []int{2, 3, 4}, // indices of 3, 4, and 5
			err:     nil,
		},
		{
			name: "Find all with k < 0",
			inds: []int{},
			f: func(x float64) bool {
				return x%2 == 0
			},
			s:        []float64{1, 2, 3, 4, 5, 6},
			k:        -1,
			expected: []int{1, 3, 5}, // indices of 2, 4, and 6
			err:      nil,
		},
		{
			name: "Not enough elements found",
			inds: []int{},
			f: func(x float64) bool {
				return x > 10
			},
			s:        []float64{1, 2, 3, 4, 5},
			k:        2,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
		{
			name: "k is zero",
			inds: []int{},
			f: func(x float64) bool {
				return x > 0
			},
			s:        []float64{1, 2, 3, 4, 5},
			k:        0,
			expected: []int{}, // should return empty slice
			err:      nil,
		},
		{
			name: "Empty slice",
			inds: []int{},
			f: func(x float64) bool {
				return x > 0
			},
			s:        []float64{},
			k:        1,
			expected: []int{},
			err:      errors.New("floats: insufficient elements found"),
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			got, err := Find(tt.inds, tt.f, tt.s, tt.k)

			if !equalSlices(got, tt.expected) {
				t.Errorf("got %v, want %v", got, tt.expected)
			}

			if (err == nil) != (tt.err == nil) || (err != nil && err.Error() != tt.err.Error()) {
				t.Errorf("got error %v, want %v", err, tt.err)
			}
		})
	}
}

// Helper function to compare slices
func equalSlices(a, b []int) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

