package floats

import (
	"math"
	"testing"
)

func TestSpan(t *testing.T) {
	t.Run("Valid span", func(t *testing.T) {
		dst := make([]float64, 5)
		result := Span(dst, 0, 10)
		expected := []float64{0, 2.5, 5, 7.5, 10}

		for i, v := range expected {
			if result[i] != v {
				t.Errorf("expected %v, got %v", expected, result)
			}
		}
	})

	t.Run("Panics on length less than 2", func(t *testing.T) {
		defer func() {
			if r := recover(); r == nil {
				t.Errorf("Expected panic but did not panic")
			}
		}()
		Span(make([]float64, 1), 0, 10)
	})

	t.Run("Handles NaN values", func(t *testing.T) {
		dst := make([]float64, 5)
		result := Span(dst, math.NaN(), 10)
		expected := []float64{math.NaN(), math.NaN(), math.NaN(), math.NaN(), 10}

		for i, v := range expected {
			if math.IsNaN(result[i]) && !math.IsNaN(v) {
				t.Errorf("expected %v, got %v", expected, result)
			} else if result[i] != v {
				t.Errorf("expected %v, got %v", expected, result)
			}
		}
	})

	t.Run("Handles Inf values", func(t *testing.T) {
		dst := make([]float64, 5)

		// l is -Inf, u is Inf
		result := Span(dst, math.Inf(-1), math.Inf(1))
		expected := []float64{math.Inf(-1), 0, 0, 0, math.Inf(1)}

		for i, v := range expected {
			if result[i] != v {
				t.Errorf("expected %v, got %v", expected, result)
			}
		}

		// l is Inf, u is 10
		result = Span(dst, math.Inf(1), 10)
		expected = []float64{math.Inf(1), 10, 10, 10, 10}

		for i, v := range expected {
			if result[i] != v {
				t.Errorf("expected %v, got %v", expected, result)
			}
		}
	})
}

