func TestSpan(t *testing.T) {
	tests := []struct {
		dst    []float64
		l, u   float64
		expected []float64
		expectPanic bool
	}{
		{
			dst:        make([]float64, 5),
			l:          0,
			u:          4,
			expected:   []float64{0, 1, 2, 3, 4},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 3),
			l:          1,
			u:          1,
			expected:   []float64{1, 1, 1},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 5),
			l:          -1,
			u:          1,
			expected:   []float64{-1, -0.5, 0, 0.5, 1},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 2),
			l:          2,
			u:          2,
			expected:   []float64{2, 2},
			expectPanic: false,
		},
		{
			dst:        make([]float64, 1),
			l:          0,
			u:          1,
			expected:   nil,
			expectPanic: true, // Should panic since length of dst < 2
		},
	}

	for _, tt := range tests {
		if tt.expectPanic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("expected panic for input %v, %v, %v", tt.dst, tt.l, tt.u)
				}
			}()
		}
		result := Span(tt.dst, tt.l, tt.u)

		if !tt.expectPanic {
			if !equalSlices(result, tt.expected) {
				t.Errorf("expected %v, got %v", tt.expected, result)
			}
		}
	}
}

func equalSlices(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if !equalFloat(a[i], b[i]) {
			return false
		}
	}
	return true
}

func equalFloat(a, b float64) bool {
	return (a == b) || (math.IsNaN(a) && math.IsNaN(b))
}

