			result := LogSumExp(test.input)
			if !almostEqual(result, test.expected) {
				t.Errorf("For input: %#v, expected: %v, got: %v", test.input, test.expected, result)
			}
		}
	}
}

// Helper function to compare float64 values for equality within a tolerance
func almostEqual(a, b float64) bool {
	const epsilon = 1e-10
	return math.Abs(a-b) < epsilon
}

