package floats

import (
	"reflect"
	"testing"
)

func TestArgsort(t *testing.T) {
	tests := []struct {
		dst      []float64
		expected []float64
		inds     []int
		expectedInds []int
	}{
		{
			dst:      []float64{3.0, 1.0, 2.0},
			expected: []float64{1.0, 2.0, 3.0},
			inds:     []int{0, 0, 0}, // need to initialize inds to the same length as dst
			expectedInds: []int{1, 2, 0},
		},
		{
			dst:      []float64{10.0, 1.0, 5.0, 2.0},
			expected: []float64{1.0, 2.0, 5.0, 10.0},
			inds:     []int{0, 0, 0, 0},
			expectedInds: []int{1, 3, 2, 0},
		},
		{
			dst:      []float64{-2.0, -1.0, 0.0, 1.0},
			expected: []float64{-2.0, -1.0, 0.0, 1.0},
			inds:     []int{0, 0, 0, 0},
			expectedInds: []int{0, 1, 2, 3},
		},
	}

	for _, test := range tests {
		// Initialize inds with the correct size
		inds := make([]int, len(test.dst))
		for i := range inds {
			inds[i] = i
		}

		// Call Argsort
		Argsort(test.dst, inds)

		// Check dst and inds against expected values
		if !reflect.DeepEqual(test.dst, test.expected) {
			t.Errorf("expected %v, got %v", test.expected, test.dst)
		}
		if !reflect.DeepEqual(inds, test.expectedInds) {
			t.Errorf("expected %v, got %v", test.expectedInds, inds)
		}
	}
}

