package floats

import (
	"testing"
)

func TestAddTo(t *testing.T) {
	tests := []struct {
		dst      []float64
		s        []float64
		t        []float64
		expected []float64
		panic    bool
	}{
		{
			dst:      []float64{0, 0, 0},
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			expected: []float64{5, 7, 9},
			panic:    false,
		},
		{
			dst:      []float64{0, 0},
			s:        []float64{1, 2},
			t:        []float64{3, 4},
			expected: []float64{4, 6},
			panic:    false,
		},
		{
			dst:      []float64{0, 0, 0},
			s:        []float64{1, 2},
			t:        []float64{3, 4},
			expected: nil, // Should panic
			panic:    true,
		},
		{
			dst:      []float64{0},
			s:        []float64{5},
			t:        []float64{10},
			expected: []float64{15},
			panic:    false,
		},
		{
			dst:      []float64{},
			s:        []float64{1, 2, 3},
			t:        []float64{4, 5, 6},
			expected: nil, // Should panic
			panic:    true,
		},
	}

	for _, tt := range tests {
		if tt.panic {
			defer func() {
				if r := recover(); r == nil {
					t.Errorf("Expected panic, but did not panick")
				}
			}()
		}
		result := AddTo(tt.dst, tt.s, tt.t)
		if !tt.panic && !equal(result, tt.expected) {
			t.Errorf("AddTo(%v, %v, %v) = %v; expected %v", tt.dst, tt.s, tt.t, result, tt.expected)
		}
	}
}

// Helper function to compare slices
func equal(a, b []float64) bool {
	if len(a) != len(b) {
		return false
	}
	for i := range a {
		if a[i] != b[i] {
			return false
		}
	}
	return true
}

