package floats

import (
	"math"
	"testing"
)

func TestNearestIdxForSpan(t *testing.T) {
	tests := []struct {
		n      int
		l, u   float64
		v      float64
		expect int
	}{
		{5, 0, 10, 2, 1}, // Middle of the range
		{5, 0, 10, -1, 0}, // Below the range
		{5, 0, 10, 11, 4}, // Above the range
		{5, 0, 10, 10, 4}, // Upper bound
		{5, 0, 10, 0, 0},  // Lower bound
		{5, -5, 5, 0, 2},  // Middle of a negative to positive range
		{3, -3, 3, -1, 1}, // Negative range, closer to zero
		{3, -3, -1, -2, 1}, // Negative range, negative value
		{3, 1, 3, 1, 0},   // Lower bound at positive limit
		{3, 1, 3, 3, 2},   // Upper bound at positive limit
		{3, math.Inf(-1), math.Inf(1), 0, 1}, // Inf range
		{3, math.Inf(1), math.Inf(-1), 0, 0}, // Negative Inf range
	}

	for _, test := range tests {
		got := NearestIdxForSpan(test.n, test.l, test.u, test.v)
		if got != test.expect {
			t.Errorf("NearestIdxForSpan(%d, %f, %f, %f) = %d; want %d", test.n, test.l, test.u, test.v, got, test.expect)
		}
	}

	// Test panic for invalid input
	defer func() {
		if recover() == nil {
			t.Errorf("Expected panic for n < 2 did not occur")
		}
	}()
	NearestIdxForSpan(1, 0, 10, 5) // This should panic
}

