package floats

import (
	"math"
	"testing"
)

func TestNearestIdxForSpan(t *testing.T) {
	tests := []struct {
		n      int
		l      float64
		u      float64
		v      float64
		expect int
	}{
		{5, 0, 10, 5, 2},     // Test in the middle
		{5, 0, 10, 0, 0},     // Test at lower bound
		{5, 0, 10, 10, 4},    // Test at upper bound
		{5, 0, 10, -1, 0},    // Test below lower bound
		{5, 0, 10, 11, 4},    // Test above upper bound
		{3, -5, 5, 0, 1},     // Test with negative bounds
		{3, -5, -1, -3, 1},   // Test with negative v inside bounds
		{3, -5, -1, -6, 0},   // Test with negative v outside lower bound
		{3, -5, -1, 0, 2},    // Test with positive v outside upper bound
		{5, math.NaN(), 5, 3, 4}, // Test NaN lower bound
		{5, 0, math.NaN(), 3, 0}, // Test NaN upper bound
		{5, math.Inf(-1), math.Inf(1), 0, 2}, // Test +/- Inf bounds
		{5, math.Inf(-1), math.Inf(1), math.Inf(-1), 0}, // Test v == -Inf
		{5, math.Inf(-1), math.Inf(1), math.Inf(1), 4}, // Test v == Inf
		{2, 0, 1, 0.5, 0}, // Edge case: n==2
	}

	for _, test := range tests {
		t.Run("", func(t *testing.T) {
			if got := NearestIdxForSpan(test.n, test.l, test.u, test.v); got != test.expect {
				t.Errorf("NearestIdxForSpan(%d, %f, %f, %f) = %d; want %d", test.n, test.l, test.u, test.v, got, test.expect)
			}
		})
	}

	// Test panic on n < 2
	defer func() {
		if r := recover(); r == nil {
			t.Errorf("NearestIdxForSpan did not panic when n < 2")
		}
	}()
	NearestIdxForSpan(1, 0, 1, 0)
}
